    },
    "href": "http:\/\/localhost\/maximo\/oslc\/os\/mxapiasset?_lid=wilson&_lpwd=wilson&oslc.pageSize=2&oslc.select=assetnum,rel.openwo{wonum,description}&oslc.where=openwo.wonum=%22*%22",
    "pagenum": 1
