          "lifetodate": 0.0,
          "measureunitid": "MILES",
          "metername": "ODOM-M",
          "sincelastoverhaul": 0.0,
          "dorollover": false,
          "readingtype": "ACTUAL",
          "rolldownsource": "ASSET",
          "slidingwindowsize": 10
        }
      ],
      "_rowstamp": "36654",
      "assetnum": "A6002",
      "location": "DALTERM",
      "href": "http:\/\/localhost\/maximo\/oslc\/os\/mxapiasset\/_QTYwMDIvRkxFRVQ-",
      "status": "NOT READY"
    },
    {
      "assetmeter_collectionref": "http:\/\/localhost\/maximo\/oslc\/os\/mxapiasset\/_TDEyNTEwL0xBUkVETw--\/int_assetmeter",
      "status_description": "Not Ready",
      "description": "STAMPING MACH ",
      "_rowstamp": "36655",
      "assetnum": "L12510",
      "location": "SHIPPING",
      "href": "http:\/\/localhost\/maximo\/oslc\/os\/mxapiasset\/_TDEyNTEwL0xBUkVETw--",
      "status": "NOT READY"
    }
  ],
  "href": "http:\/\/localhost\/maximo\/oslc\/os\/mxapiasset",
  "responseInfo": {
    "nextPage": {
      "href": "http:\/\/localhost\/maximo\/oslc\/os\/mxapiasset?pageno=2&oslc.select=assetnum%2Clocation%2Cdescription%2Cstatus%2Cassetmeter%7B*%7D&lean=1&oslc.pageSize=2"
    },
    "href": "http:\/\/localhost\/maximo\/oslc\/os\/mxapiasset?oslc.pageSize=10&lean=1&oslc.select=assetnum,location,description,status,assetmeter{*}",
    "pagenum": 1
  }
